import React from "react";
import styled from "styled-components";

import homeBg1 from "../../Assets/homeBg1.jpg";
import homeBg2 from "../../Assets/homeBg2.jpeg";

const Hero = () => {
  return (
    <HeroContainer>
      <img src={homeBg1} alt="Art and Store" className="bgimage"></img>
      <div className="hero-info-container">
        <h2>Subtitle</h2>
        <h1>Main Title</h1>
        <p>Lorem ipsum dolor sit amet consectetur.</p>
      </div>
    </HeroContainer>
  );
};

const HeroContainer = styled.section`
  min-height: 60vh;
  display: grid;
  place-items: center;
  position: relative;
  .bgimage {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
  @media (min-width: 992px) {
    height: calc(100vh - 5rem);
    grid-template-columns: 1fr 1fr;
    gap: 8rem;
    h1 {
      margin-bottom: 2rem;
    }
    p {
      font-size: 1.25rem;
    }
    .hero-btn {
      padding: 0.75rem 1.5rem;
      font-size: 1rem;
    }
    .img-container {
      display: block;
      position: relative;
    }
    .main-img {
      width: 100%;
      height: 550px;
      position: relative;
      border-radius: var(--radius);
      display: block;
      object-fit: cover;
    }
    .accent-img {
      position: absolute;
      bottom: 0;
      left: 0;
      width: 250px;
      transform: translateX(-50%);
      border-radius: var(--radius);
    }
    .img-container::before {
      content: "";
      position: absolute;
      width: 10%;
      height: 80%;
      background: var(--clr-primary-9);
      bottom: 0%;
      left: -8%;
      border-radius: var(--radius);
    }
  }
`;

export default Hero;
