import React from "react";
import styled from "styled-components";

import homeBg1 from "../../Assets/homeBg1.jpg";
import homeBg2 from "../../Assets/homeBg2.jpeg";

const Hero = () => {
  return (
    <HeroContainer>
      <div className="bgimage-overlay"></div>
      <img src={homeBg1} alt="Art and Store" className="bgimage"></img>
      <div className="hero-info-container">
        <h2>Subtitle</h2>
        <h1>Main Title</h1>
        <p>Lorem ipsum dolor sit amet consectetur.</p>
      </div>
    </HeroContainer>
  );
};

const HeroContainer = styled.section`
  min-height: 60vh;
  position: relative;
  .bgimage-overlay {
    width: 100%;
    height: 99.2%;
    background: black;
    opacity: 0.45;
    position: absolute;
    top: 0;
    left: 0;
    z-index: 100;
  }
  .bgimage {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
  .hero-info-container {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    text-align: center;
    color: var(--ColorWhite);
    z-index: 200;
    h2 {
      font-family: var(--FontWork);
    }
    h1 {
      font-family: var(--FontWork);
      font-size: 5rem;
      letter-spacing: 1px;
      margin-top: --1rem;
    }
    p {
      font-family: var(--FontLora);
    }
  }
  @media (min-width: 1150px) {
  }
`;

export default Hero;
