import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";

import homeBg1 from "../../Assets/homeBg1.jpg";
import homeBg2 from "../../Assets/homeBg2.jpeg";

const Hero = () => {
  return (
    <HeroContainer>
      <div className="img-container">
        <div className="bgimage-overlay"></div>
        <img src={homeBg1} alt="Art and Store" className="bgimage"></img>
      </div>
      <div className="hero-info-container">
        <h2>Art & Store</h2>
        <h1>Transform your spaces</h1>
      </div>
      <Link to="/products" className="explore-btn">
        Explore Collection
      </Link>
    </HeroContainer>
  );
};

const HeroContainer = styled.section`
  min-height: 60vh;
  position: relative;
  .img-container {
    width: 100%;
    height: 100%;
  }
  .bgimage-overlay {
    width: 100%;
    height: 100%;
    background: black;
    opacity: 0.35;
    position: absolute;
    top: 0;
    left: 0;
    z-index: 100;
  }
  .bgimage {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
  .hero-info-container {
    position: absolute;
    top: 30%;
    left: 50%;
    transform: translate(-50%, -30%);
    text-align: center;
    color: var(--ColorWhite);
    z-index: 200;
    h2 {
      font-family: var(--FontLora);
      font-size: 1rem;
      font-weight: 100;
    }
    h1 {
      /* font-family: var(--FontWork); */
      font-size: 4rem;
      letter-spacing: 1px;
      font-weight: 100;
      margin-top: -0.5rem;
    }
  }
  .explore-btn {
    border: none;
    outline: none;
    position: absolute;
    bottom: 12%;
    left: 50%;
    transform: translate(-50%, -12%);
    z-index: 200;
    font-family: var(--FontWork);
    font-size: 1.5rem;
    text-align: center;
    font-weight: 300;
    background: var(--ColorWhite);
    padding: 0.7rem 2.7rem;
    color: var(--ColorSemiCrimson);
    box-shadow: var(--ShadowLight);
    transition: var(--MainTransition);
    &:hover {
      background: var(--ColorPiel);
      color: var(--ColorWhite);
      box-shadow: var(--ShadowDark);
    }
  }
  @media (min-width: 800px) {
    height: 77vh;
    .hero-info-container {
      top: 39%;
      transform: translate(-50%, -39%);
      h2 {
        font-size: 1.8rem;
      }
      h1 {
        font-size: 5.5rem;
        font-weight: 400;
      }
    }
    .explore-btn {
      padding: 2.5rem 3.5rem;
      font-size: 1.7rem;
    }
  }
`;

export default Hero;
