import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import { FaTimes } from "react-icons/fa";

import { Logotype } from "../Logotype";
import { UserButtons } from "../Navigation";
import { links } from "../../Utils/constants";

const Sidebar = () => {
  const isOpen = true;
  return (
    <SidebarContainer>
      <aside className={`${isOpen ? "sidebar show-sidebar" : "show-sidebar"}`}>
        <div className="sidebar-header">
          <Logotype></Logotype>
          <button className="close-btn" type="button">
            <FaTimes></FaTimes>
          </button>
        </div>
        <ul className="links">
          {links.map((singleLink) => {
            const { id, name, url } = singleLink;
            return (
              <li key={id}>
                <Link to={url}>{name}</Link>
              </li>
            );
          })}
          <li>
            <Link to="/checkout">checkout</Link>
          </li>
        </ul>
        <UserButtons></UserButtons>
      </aside>
    </SidebarContainer>
  );
};

const SidebarContainer = styled.div`
  text-align: center;
  /* display: none; */
  .sidebar-header {
    display: flex;
    justify-content: space-between;
    align-items: baseline;
    width: 87%;
    margin: 0 auto;
    padding-top: 1.4rem;
  }
  .close-btn {
    font-size: 1.7rem;
    background: transparent;
    border-color: transparent;
    color: var(--ColorFooterDivider) !important;
    transition: var(--TransitionFast);
    cursor: pointer;
    margin-top: 0.2rem;
  }
  .close-btn:hover {
    color: var(--ColorSemiCrimson) !important;
  }
  .links {
    margin-bottom: 2rem;
  }
  .links a {
    display: block;
    text-align: left;
    font-size: 1.2rem;
    text-transform: capitalize;
    padding: 1rem 1.5rem;
    color: var(--FontColorGrey);
    transition: var(--MainTransition);
    letter-spacing: 0.7px;
  }
  .links a:hover {
    padding: 1rem 1.5rem;
    padding-left: 2rem;
    background: var(--ColorBlack-35);
    color: var(--clr-grey-2);
  }

  .sidebar {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: var(--ColorWhite);
    transition: var(--MainTransition);
    transform: translate(-100%);
    z-index: -1;
  }
  .show-sidebar {
    transform: translate(0);
    z-index: 999;
  }
  .cart-btn-wrapper {
    margin: 2rem auto;
  }
  @media screen and (min-width: 992px) {
    .sidebar {
      display: none;
    }
  }
`;

export default Sidebar;
